import React from 'react';
import './CustomerViewBooking.css';

function CustomerViewBooking() {
  const bookings = [
    { id: 1, treatmentName: 'Dental Checkup', date: '2023-07-01', status: 'Confirmed' },
    { id: 2, treatmentName: 'Teeth Cleaning', date: '2023-07-03', status: 'Pending' },
    { id: 3, treatmentName: 'Root Canal', date: '2023-07-05', status: 'Confirmed' },
  ];

  const handleCancelBooking = (bookingId) => {
    // Handle cancel booking logic
    alert(`Cancel booking with ID: ${bookingId}`);
  };

  const handleEditBooking = (bookingId) => {
    // Handle edit booking logic
    alert(`Edit booking with ID: ${bookingId}`);
  };

  return (
    <div className="customer-view-booking">
      <h2>My Bookings</h2>
      <div className="booking-list">
        {bookings.map((booking) => (
          <div className="booking-form" key={booking.id}>
            <div className="booking-details">
              <div>
                <span className="label">Treatment:</span>
                <span className="value">{booking.treatmentName}</span>
              </div>
              <div>
                <span className="label">Date:</span>
                <span className="value">{booking.date}</span>
              </div>
              <div>
                <span className="label">Status:</span>
                <span className="value">{booking.status}</span>
              </div>
            </div>
            <div className="dropdown-menu">
              <select>
                <option disabled selected>Select Action</option>
                <option value="cancel" onClick={() => handleCancelBooking(booking.id)}>
                  Cancel Booking
                </option>
                <option value="edit" onClick={() => handleEditBooking(booking.id)}>
                  Edit Booking
                </option>
              </select>
            </div>
          </div>
        ))}
      </div>
    </div>
  );
}

export default CustomerViewBooking;
