import React, { useState } from 'react';
import './CustomerViewBooking.css';
import ViewBookingDetail from './ViewBookingDetail';

function CustomerViewBooking() {
  const bookings = [
    {
      id: 1,
      name: 'John Doe',
      treatmentDate: '2023-07-01',
      treatmentType: 'Dental Checkup',
      time: '10:00 AM',
      description: 'Regular dental checkup and cleaning',
      status: 'Confirmed',
      doctorName: 'Dr. Smith',
    },
    // Add more booking data as needed
  ];

  const [selectedBooking, setSelectedBooking] = useState(null);

  const handleViewBookingDetail = (booking) => {
    setSelectedBooking(booking);
  };

  const handlePopupClose = () => {
    setSelectedBooking(null);
  };

  return (
    <div className="customer-view-booking">
      <h2>My Bookings</h2>
      <div className="booking-list">
        {bookings.map((booking) => (
          <div className="booking-form" key={booking.id}>
            <div className="booking-details">
              <div>
                <span className="label">Name:</span>
                <span className="value">{booking.name}</span>
              </div>
              <div>
                <span className="label">Treatment Date:</span>
                <span className="value">{booking.treatmentDate}</span>
              </div>
              <div>
                <span className="label">Treatment Type:</span>
                <span className="value">{booking.treatmentType}</span>
              </div>
              <div>
                <span className="label">Time:</span>
                <span className="value">{booking.time}</span>
              </div>
              <div>
                <span className="label">Status:</span>
                <span className="value">{booking.status}</span>
              </div>
            </div>
            <div className="booking-actions">
              <button onClick={() => handleViewBookingDetail(booking)}>View Details</button>
            </div>
          </div>
        ))}
      </div>
      {selectedBooking && (
        <ViewBookingDetail booking={selectedBooking} onClose={handlePopupClose} />
      )}
    </div>
  );
}

export default CustomerViewBooking;
