import React, { useState } from 'react';
import './CancellationRequests.css';

const CancellationRequests = () => {
  const [currentPendingPage, setCurrentPendingPage] = useState(1);
  const [currentApprovedPage, setCurrentApprovedPage] = useState(1);
  const requestsPerPage = 3;

  const cancellationRequests = [
    {
        id: 1,
        patientName: 'John Doe',
        doctorName: 'Dr. Emily Nguyen',
        reason: 'Emergency situation',
        status: 'Pending',
        date: '2023-07-01',
      },
      {
        id: 2,
        patientName: 'Jane Smith',
        doctorName: 'Dr. David Johnson',
        reason: 'Change of schedule',
        status: 'Pending',
        date: '2023-07-02',
      },
      {
        id: 3,
        patientName: 'Michael Brown',
        doctorName: 'Dr. Sophia Lee',
        reason: 'Family emergency',
        status: 'Pending',
        date: '2023-07-03',
      },
      {
        id: 4,
        patientName: 'Emma Wilson',
        doctorName: 'Dr. Jacob Anderson',
        reason: 'Illness',
        status: 'Pending',
        date: '2023-07-04',
      },
      {
        id: 5,
        patientName: 'Oliver Taylor',
        doctorName: 'Dr. Olivia Martin',
        reason: 'Unexpected travel',
        status: 'Pending',
        date: '2023-07-05',
      },
      {
        id: 6,
        patientName: 'Sophia Allen',
        doctorName: 'Dr. Ethan Garcia',
        reason: 'Change of plans',
        status: 'Approved',
        date: '2023-07-01',
      },
      {
        id: 7,
        patientName: 'Noah Walker',
        doctorName: 'Dr. Ava Robinson',
        reason: 'Work commitment',
        status: 'Approved',
        date: '2023-07-02',
      },
      {
        id: 8,
        patientName: 'Isabella Clark',
        doctorName: 'Dr. Logan Mitchell',
        reason: 'Personal reasons',
        status: 'Approved',
        date: '2023-07-03',
      },
      {
        id: 9,
        patientName: 'James Baker',
        doctorName: 'Dr. Mia Nelson',
        reason: 'Unforeseen circumstances',
        status: 'Approved',
        date: '2023-07-04',
      },
      {
        id: 10,
        patientName: 'Charlotte Turner',
        doctorName: 'Dr. Noah Martinez',
        reason: 'Other commitments',
        status: 'Approved',
        date: '2023-07-05',
      },
  ];

  const pendingRequests = cancellationRequests.filter(
    (request) => request.status === 'Pending'
  );
  const approvedRequests = cancellationRequests.filter(
    (request) => request.status === 'Approved'
  );

  const indexOfLastPendingRequest = currentPendingPage * requestsPerPage;
  const indexOfFirstPendingRequest = indexOfLastPendingRequest - requestsPerPage;
  const currentPendingRequests = pendingRequests.slice(
    indexOfFirstPendingRequest,
    indexOfLastPendingRequest
  );

  const indexOfLastApprovedRequest = currentApprovedPage * requestsPerPage;
  const indexOfFirstApprovedRequest = indexOfLastApprovedRequest - requestsPerPage;
  const currentApprovedRequests = approvedRequests.slice(
    indexOfFirstApprovedRequest,
    indexOfLastApprovedRequest
  );

  const nextPendingPage = () => setCurrentPendingPage(currentPendingPage + 1);
  const previousPendingPage = () => setCurrentPendingPage(currentPendingPage - 1);

  const nextApprovedPage = () => setCurrentApprovedPage(currentApprovedPage + 1);
  const previousApprovedPage = () => setCurrentApprovedPage(currentApprovedPage - 1);

  return (
    <div className="cancellation-requests">
      <div className="requests-container">
        <div className="requests-section">
          <h2>Đang chờ phê duyệt</h2>
          <div className="request-list pending-requests">
            {currentPendingRequests.map((request) => (
              <div key={request.id} className="request-item">
                <div className="avatar"></div>
                <div className="request-info">
                  <p className="patient-name">{request.patientName}</p>
                  <p className="doctor-name">Bác sĩ: {request.doctorName}</p>
                  <p className="reason">Lý do hủy: {request.reason}</p>
                </div>
                <div className="request-status">
                  <p className="status-pending">Đang chờ phê duyệt</p>
                  <p className="date">Ngày: {request.date}</p>
                  <div className="actions">
                    <button className="reject-button">Từ chối</button>
                    <button className="approve-button">Duyệt</button>
                  </div>
                </div>
              </div>
            ))}
          </div>
          <div className="pagination">
            {currentPendingPage > 1 && (
              <button className="pagination-button" onClick={previousPendingPage}>
                &lt;
              </button>
            )}
            {pendingRequests.length > indexOfLastPendingRequest && (
              <button className="pagination-button" onClick={nextPendingPage}>
                &gt;
              </button>
            )}
          </div>
        </div>
        <div className="requests-section">
          <h2>Đơn đã phê duyệt</h2>
          <div className="request-list approved-requests">
            {currentApprovedRequests.map((request) => (
              <div key={request.id} className="request-item">
                <div className="avatar"></div>
                <div className="request-info">
                  <p className="patient-name">{request.patientName}</p>
                  <p className="doctor-name">Bác sĩ: {request.doctorName}</p>
                  <p className="reason">Lý do hủy: {request.reason}</p>
                </div>
                <div className="request-status">
                  <p className="status-approved">Đã duyệt</p>
                  <p className="date">Ngày: {request.date}</p>
                  <div className="actions">
                    <p className="action">Đã duyệt</p>
                  </div>
                </div>
              </div>
            ))}
          </div>
          <div className="pagination">
            {currentApprovedPage > 1 && (
              <button className="pagination-button" onClick={previousApprovedPage}>
                &lt;
              </button>
            )}
            {approvedRequests.length > indexOfLastApprovedRequest && (
              <button className="pagination-button" onClick={nextApprovedPage}>
                &gt;
              </button>
            )}
          </div>
        </div>
      </div>
    </div>
  );
};

export default CancellationRequests;
