import React, { useState } from 'react';
import './CancellationRequests.css';

const CancellationRequests = () => {
  const [requests, setRequests] = useState([
    {
      id: 1,
      patientName: 'John Doe',
      doctorName: 'Dr. Emily Nguyen',
      reason: 'Emergency situation',
      status: 'Pending',
      date: '2023-07-01',
    },
    {
      id: 2,
      patientName: 'Jane Smith',
      doctorName: 'Dr. David Johnson',
      reason: 'Change of schedule',
      status: 'Pending',
      date: '2023-07-02',
    },
    {
      id: 3,
      patientName: 'Michael Brown',
      doctorName: 'Dr. Sophia Lee',
      reason: 'Family emergency',
      status: 'Pending',
      date: '2023-07-03',
    },
    {
      id: 4,
      patientName: 'Emma Wilson',
      doctorName: 'Dr. Jacob Anderson',
      reason: 'Illness',
      status: 'Pending',
      date: '2023-07-04',
    },
    {
      id: 5,
      patientName: 'Oliver Taylor',
      doctorName: 'Dr. Olivia Martin',
      reason: 'Unexpected travel',
      status: 'Pending',
      date: '2023-07-05',
    },
    {
      id: 6,
      patientName: 'Sophia Allen',
      doctorName: 'Dr. Ethan Garcia',
      reason: 'Change of plans',
      status: 'Approved',
      date: '2023-07-01',
    },
    {
      id: 7,
      patientName: 'Noah Walker',
      doctorName: 'Dr. Ava Robinson',
      reason: 'Work commitment',
      status: 'Approved',
      date: '2023-07-02',
    },
    {
      id: 8,
      patientName: 'Isabella Clark',
      doctorName: 'Dr. Logan Mitchell',
      reason: 'Personal reasons',
      status: 'Approved',
      date: '2023-07-03',
    },
    {
      id: 9,
      patientName: 'James Baker',
      doctorName: 'Dr. Mia Nelson',
      reason: 'Unforeseen circumstances',
      status: 'Approved',
      date: '2023-07-04',
    },
    {
      id: 10,
      patientName: 'Charlotte Turner',
      doctorName: 'Dr. Noah Martinez',
      reason: 'Other commitments',
      status: 'Approved',
      date: '2023-07-05',
    },
  ]);

  const [currentPage, setCurrentPage] = useState(1);
  const itemsPerPage = 3;
  const totalPages = Math.ceil(requests.length / itemsPerPage);
  const indexOfLastItem = currentPage * itemsPerPage;
  const indexOfFirstItem = indexOfLastItem - itemsPerPage;
  const currentItems = requests.slice(indexOfFirstItem, indexOfLastItem);

  const handleApproveRequest = (id) => {
    const updatedRequests = requests.map((request) => {
      if (request.id === id) {
        return { ...request, status: 'Approved' };
      }
      return request;
    });
    setRequests(updatedRequests);
  };

  const handleRejectRequest = (id) => {
    const updatedRequests = requests.map((request) => {
      if (request.id === id) {
        return { ...request, status: 'Rejected' };
      }
      return request;
    });
    setRequests(updatedRequests);
  };

  const handlePageChange = (pageNumber) => {
    setCurrentPage(pageNumber);
  };

  return (
    <div className="cancellation-requests">
      <div className="requests-container">
        <div className="requests-section">
          <h2>Đang chờ phê duyệt</h2>
          {currentItems
            .filter((request) => request.status === 'Pending')
            .map((request) => (
              <div className="request-item" key={request.id}>
                <div className="request-info">
                  <div className="patient-info">
                    <p className="patient-name">{request.patientName}</p>
                    <p className="doctor-name">{request.doctorName}</p>
                  </div>
                  <div className="reason">{request.reason}</div>
                </div>
                <div className="request-date">{request.date}</div>
                <div className="request-actions">
                  <button
                    className="approve-button"
                    onClick={() => handleApproveRequest(request.id)}
                  >
                    Duyệt
                  </button>
                  <button
                    className="reject-button"
                    onClick={() => handleRejectRequest(request.id)}
                  >
                    Từ chối
                  </button>
                </div>
              </div>
            ))}
          {currentItems
            .filter((request) => request.status === 'Pending')
            .length === 0 && (
              <div className="no-requests">Không có đơn chờ phê duyệt.</div>
            )}
          <div className="pagination">
            {currentPage > 1 && (
              <button onClick={() => handlePageChange(currentPage - 1)}>←</button>
            )}
            {currentPage < totalPages && (
              <button onClick={() => handlePageChange(currentPage + 1)}>→</button>
            )}
          </div>
        </div>
        <div className="requests-section">
          <h2>Đơn đã phê duyệt</h2>
          {currentItems
            .filter((request) => request.status !== 'Pending')
            .map((request) => (
              <div className="request-item" key={request.id}>
                <div className="request-info">
                  <div className="patient-info">
                    <p className="patient-name">{request.patientName}</p>
                    <p className="doctor-name">{request.doctorName}</p>
                  </div>
                  <div className="reason">{request.reason}</div>
                </div>
                <div className="request-date">{request.date}</div>
                <div className="request-status">{request.status}</div>
              </div>
            ))}
          {currentItems
            .filter((request) => request.status !== 'Pending')
            .length === 0 && (
              <div className="no-requests">Không có đơn đã phê duyệt.</div>
            )}
          <div className="pagination">
            {currentPage > 1 && (
              <button onClick={() => handlePageChange(currentPage - 1)}>←</button>
            )}
            {currentPage < totalPages && (
              <button onClick={() => handlePageChange(currentPage + 1)}>→</button>
            )}
          </div>
        </div>
      </div>
    </div>
  );
};

export default CancellationRequests;
